mapper.select:
SELECT rnum, MEM_ID, RV_CONTENT, RV_SCORE, RV_DATE, P_NO, RV_IMG, RV_IMG2, RV_IMG3, MEM_NICKNAME
        FROM(
            SELECT ROWNUM as rnum, a.*  FROM (
                SELECT MEM_ID, RV_CONTENT, RV_SCORE, RV_DATE, P_NO, RV_IMG, RV_IMG2, RV_IMG3, MEM_NICKNAME 
                FROM TB_REVIEW WHERE P_NO = #{p_no}
            ) a
        )
WHERE #{offset} < rnum  and rnum <= #{offsetTo}

SELECT rnum, MEM_ID, RV_CONTENT, RV_SCORE, RV_DATE, P_NO, RV_IMG, RV_IMG2, RV_IMG3, MEM_NICKNAME
        FROM(
            SELECT ROWNUM as rnum, a.*  FROM (
                SELECT MEM_ID, RV_CONTENT, RV_SCORE, RV_DATE, P_NO, RV_IMG, RV_IMG2, RV_IMG3, MEM_NICKNAME 
                FROM TB_REVIEW WHERE P_NO = #{p_no}
            ) a
        )
WHERE #{offset} < rnum  and rnum <= #{offsetTo}

		SELECT rnum, MEM_ID, RV_CONTENT, RV_SCORE, RV_DATE, P_NO, RV_IMG, RV_IMG2, RV_IMG3, MEM_NICKNAME
                FROM(
                    SELECT ROWNUM as rnum, a.*  FROM (
                        SELECT MEM_ID, RV_CONTENT, RV_SCORE, RV_DATE, P_NO, RV_IMG, RV_IMG2, RV_IMG3, MEM_NICKNAME 
                        FROM TB_REVIEW WHERE P_NO = #{p_no} AND MEM_ID = #{mem_id}
                    ) a
                )
        WHERE rnum = 1

choose.when:
UPDATE TB_REVIEW 
SET RV_CONTENT = #{rv_content}, RV_SCORE = #{rv_score}, RV_DATE = SYSDATE, RV_IMG = #{rv_image}
WHERE RV_CONTENT = (
	SELECT RV_CONTENT
	 FROM(
		SELECT ROWNUM as rnum, RV_CONTENT FROM (
			SELECT RV_CONTENT FROM TB_REVIEW WHERE P_NO = #{p_no} AND MEM_ID = #{mem_id}
			)
	)WHERE rnum = 1)

UPDATE TB_REVIEW 
SET RV_CONTENT = #{rv_content}, RV_SCORE = #{rv_score}, RV_DATE = SYSDATE, RV_IMG = #{rv_image}, RV_IMG2 = #{rv_image2}
WHERE RV_CONTENT = (
	SELECT RV_CONTENT
	 FROM(
		SELECT ROWNUM as rnum, RV_CONTENT FROM (
			SELECT RV_CONTENT FROM TB_REVIEW WHERE P_NO = #{p_no} AND MEM_ID = #{mem_id}
			)
	)WHERE rnum = 1)

UPDATE TB_REVIEW 
SET RV_CONTENT = #{rv_content}, RV_SCORE = #{rv_score}, RV_DATE = SYSDATE, RV_IMG = #{rv_image}, RV_IMG2 = #{rv_image2}, RV_IMG3 = #{rv_image3}
WHERE RV_CONTENT = (
	SELECT RV_CONTENT
	 FROM(
		SELECT ROWNUM as rnum, RV_CONTENT FROM (
			SELECT RV_CONTENT FROM TB_REVIEW WHERE P_NO = #{p_no} AND MEM_ID = #{mem_id}
			)
	)WHERE rnum = 1)

choose.otherwise:
UPDATE TB_REVIEW 
SET RV_CONTENT = #{rv_content}, RV_SCORE = #{rv_score}, RV_DATE = SYSDATE
WHERE RV_CONTENT = (
	SELECT RV_CONTENT
	 FROM(
		SELECT ROWNUM as rnum, RV_CONTENT FROM (
			SELECT RV_CONTENT FROM TB_REVIEW WHERE P_NO = #{p_no} AND MEM_ID = #{mem_id}
			)
	)WHERE rnum = 1)

mapper.delete:
DELETE FROM TB_REVIEW
WHERE P_NO = #{p_no}
AND MEM_ID = #{mem_id}
AND RV_CONTENT = #{rv_content}
AND RV_SCORE = #{rv_score}

mapper.insert:
INSERT INTO TB_PRODUCT (
						 P_NO
					   , P_TITLE
					   , P_AUTHOR
					   , P_IMG
					   , P_PUBLISHER
					   , P_PUBDATE
					   , P_PRICE
					   , P_DESCRIPTION
					   , P_CATEGORY
					   , P_COUNT
)
VALUES (
		   SEQ_P_NO.nextval
		   , #{p_title}
		   , #{p_author}
		   , #{p_img}
		   , #{p_publisher}
		   , sysdate
		   , #{p_price}
		   , #{p_description}
		   , #{p_category}
		   , #{p_count}
		   )

mapper.update:
UPDATE TB_PRODUCT 
SET P_LIKE = P_LIKE + #{value}
WHERE P_NO = #{p_no}

mapper.select:
		SELECT count(*)
        FROM(
                SELECT ROWNUM as rnum, a.*  FROM (
                     SELECT MEM_ID, MEM_NICKNAME, RV_CONTENT, RV_SCORE, RV_DATE, P_NO, RV_IMG, RV_IMG2, RV_IMG3 FROM TB_REVIEW
                     WHERE P_NO = #{value}
                 ) a
            )

SELECT last.* FROM (SELECT rownum AS rn, od.* FROM ( select * from tb_product
ORDER BY P_PUBDATE DESC ORDER BY P_TSELL DESC ORDER BY Cast(P_PRICE AS INTEGER) ASC ORDER BY Cast(P_PRICE AS INTEGER) DESC ORDER BY P_REVIEW DESC ORDER BY P_LIKE DESC ORDER BY P_AVGSCORE DESC
)od
WHERE ( P_TITLE OR P_AUTHOR )
AND P_CATEGORY = #{p_category}
WHERE P_CATEGORY = #{p_category}
) last
WHERE rn >(#{pageNum}-1) * #{amount} and rn <= #{pageNum}*#{amount}

SELECT count(*) FROM tb_product
--키워드로만 검색할 경우 WHERE (P_TITLE OR P_AUTHOR )
AND P_CATEGORY = #{p_category}
-- 타입으로만 검색할 경우 WHERE P_CATEGORY = #{p_category}

SELECT DISTINCT p_category as categoryCode FROM TB_PRODUCT
WHERE P_TITLE OR P_AUTHOR
WHERE P_CATEGORY =#{p_category}

SELECT DISTINCT count(*) as categoryCount FROM ( SELECT * FROM TB_PRODUCT
WHERE P_TITLE OR P_AUTHOR
WHERE P_CATEGORY =#{p_category}
)
